mapper.select:
select
  book_id, category_id, book_name, book_describe, author, published, price, book_status, img, ebook, location, zan_number, score, input_time, update_time
from library.book


        and book_id = #{bookId}


        and category_id = #{categoryId}


        and book_name = #{bookName}


        and book_describe = #{bookDescribe}


        and author = #{author}


        and published = #{published}


        and price = #{price}


        and book_status = #{bookStatus}


        and img = #{img}


        and ebook = #{ebook}


        and location = #{location}


        and zan_number = #{zanNumber}


        and score = #{score}


        and input_time = #{inputTime}


        and update_time = #{updateTime}
