mapper.select:
select
  borrowing_id, book_id, user_id, book_name, user_name, duration, application_time, handle_time, borrowing_time, return_time, borrowing_status, operator,note, create_time, update_time, remark1, remark2, remark3
from library.borrowing


        and borrowing_id = #{borrowingId}


        and book_id = #{bookId}


        and user_id = #{userId}


        and book_name like "%"#{bookName}"%"


        and user_name like "%"#{userName}"%"


        and duration < #{duration}


        and application_time  <  #{applicationTime}


        and handle_time  <  #{handleTime}


        and borrowing_time  <   #{borrowingTime}


        and return_time  <  #{returnTime}


        and borrowing_status = #{borrowingStatus}


        and operator = #{operator}


        and note = #{note}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remark1 = #{remark1}


        and remark2 = #{remark2}


        and remark3 = #{remark3}
