mapper.select:
select
  borrowing_id, book_id, user_id, book_name, user_name, application_time, borrowing_time, return_time, status, operator
from library.borrowing


        and borrowing_id = #{borrowingId}


        and book_id = #{bookId}


        and user_id = #{userId}


        and book_name = #{bookName}


        and user_name = #{userName}


        and application_time = #{applicationTime}


        and borrowing_time = #{borrowingTime}


        and return_time = #{returnTime}


        and status = #{status}


        and operator = #{operator}
