mapper.select:
select
  comment_id, book_id, user_id, comment_time, Zan_flag, content, score
from library.comment


        and comment_id = #{commentId}


        and book_id = #{bookId}


        and user_id = #{userId}


        and comment_time = #{commentTime}


        and Zan_flag = #{zanFlag}


        and content = #{content}


        and score = #{score}
