mapper.select:
select
  comment_id, book_id, user_id, comment_time, content, score, reply_flag, reply_id, zan_number, remark2, remark3
from library.comment


        and comment_id = #{commentId}


        and book_id = #{bookId}


        and user_id = #{userId}


        and comment_time = #{commentTime}


        and content = #{content}


        and score = #{score}


        and reply_flag = #{replyFlag}


        and reply_id = #{replyId}


        and zan_number = #{zanNumber}


        and remark2 = #{remark2}


        and remark3 = #{remark3}


        order by ${sortWay} desc
