mapper.select:
select LEFT(input_time,#{timeSlot}) '时间',count(*) '新增书籍数量' from book
and ebook = #{ebook}
GROUP BY LEFT(input_time,#{timeSlot}) ORDER BY LEFT(input_time,#{timeSlot})

select LEFT(register_time,#{timeSlot}) '时间',count(*) '新增用户数量' from user
and role = #{role}
GROUP BY LEFT(register_time,#{timeSlot}) ORDER BY LEFT(register_time,#{timeSlot})
